const PlayerStates = {
  STAND: "stand",
  RUN: "run",
  JUMP: "jump",
}

const PlayerFrames = {
  STAND: {
    height: 16,
    width: 16,
    offsetX: 80,
    offsetY: 34,
    count: 1,
  },
  RUN: {
    height: 16,
    width: 16,
    offsetX: 80,
    offsetY: 34,
    count: 1,
  },
}

const SHEET_GAP = 1;

class Player {
  constructor(context, sheet) {
    this.context = context;
    this.sheet = sheet;

    this.state = PlayerStates.RUN;

    this.frameIndex = 0;
    this.tickCount = 0;
    this.ticksPerFrame = 0;
  }

  setPlayerState(state) {
    this.state = state;
  }

  update() {

  }

  draw() {
    const state = this.state;
    let frame = PlayerFrames.STAND;
    if (state === PlayerStates.STAND) { 
      frame = PlayerFrames.STAND;
    } else if (state === PlayerStates.RUN) {
      frame = PlayerFrames.RUN;
    }
    this.context.drawImage(this.sheet,
                           frame.offsetX, frame.offsetY, frame.width, frame.height,
                           0, 0, frame.width, frame.height);
  }
}

export default Player;
