import Player from './Player';
import Connection from './Connection'


class Game {
  players = [];

  constructor(width, height, context) {
    this.width = width;
    this.height = height;
    this.context = context;
  }

  setResources(resources) {
    this.resources = resources;
  }

  init() {
    // make sure resources are loaded
    Promise.all(Object.values(this.resources).map(img => this.checkImage(img.src)))
                  .then(() => this.start());

    // create connection class
    this.connection = new Connection()
  }

  start() {
    const player = new Player(this.width, this.height, this.context, this.resources.mario);
    this.player = player;
    this.players.push(player);

    // create connection for player
    const playerName = (new URLSearchParams(window.location.search)).get('p') || "default"
    this.connection.connect(playerName)
    this.connection.subscribe((playerActions) => {
        const playerAction = playerActions.find((e) => e.name === playerName)

        // handle position
        if (playerAction.position) {
          const {x, y} = playerAction.position
          player.setPlayerPosition(x, y)
        }
        // handle direction
        if (playerAction.direction) {
          player.direction = playerAction.direction
        }
        // handle state
        if (playerAction.state) {
          player.state = playerAction.state
        }

        // check if all players created
    })

    document.onkeydown = (e) => {
      e = e || window.event;
      if (e.keyCode === 38) {
         this.connection.send("up")
      } else if (e.keyCode === 40) {
         this.connection.send("down")
      } else if (e.keyCode === 37) {
         this.connection.send("left")
      } else if (e.keyCode === 39) {
         this.connection.send("right")
      }
    }

    this.gameLoop();
  }

  drawBG() {
    this.context.fillStyle = "#5D94FB";
    this.context.fillRect(0, 0, this.width, this.height);
  }

  // main game loop -------------------------
  gameLoop () {

    window.requestAnimationFrame(() => this.gameLoop());

    this.drawBG();
    this.player.update();
    this.player.draw();
  }

  // UTILS ----------------------------------
  checkImage(path) {
    return new Promise(resolve => {
        const img = new Image();
        img.onload = () => resolve({path, status: 'ok'});
        img.onerror = () => resolve({path, status: 'error'});

        img.src = path;
    });
  }

}

export default Game;
