const PlayerStates = {
  STAND: "stand",
  RUN: "run",
  JUMP: "jump",
}

class Player {
  height = 16;
  width = 16;
  offsetX = 80;
  offsetY = 34;
  gap = 1;

  constructor(context, sheet) {
    this.context = context;
    this.sheet = sheet;

    this.state = PlayerStates.STAND;
  }

  setPlayerState(state) {
    this.state = state;
  }

  draw() {
    this.context.drawImage(this.sheet,
                           this.offsetX, this.offsetY, this.width, this.height,
                           0, 0, this.width, this.height);
  }
}

export default Player;
