const PlayerStates = {
  STAND: "stand",
  RUN: "run",
  JUMP: "jump",
}

const PlayerFrames = {
  STAND: {
    height: 16,
    width: 16,
    offsetX: 80,
    offsetY: 34,
    count: 1,
  },
  RUN: {
    height: 16,
    width: 16,
    offsetX: 80,
    offsetY: 34,
    count: 1,
  },
}

const SHEET_GAP = 1;

class Player {
  constructor(context, sheet) {
    this.context = context;
    this.sheet = sheet;

    this.state = PlayerStates.STAND;
  }

  setPlayerState(state) {
    this.state = state;
  }

  draw() {
    const state = this.state;
    if (state === PlayerStates.STAND) { 
      state.context.drawImage(state.sheet,
                              state.offsetX, state.offsetY, state.width, state.height,
                              0, 0, state.width, state.height);
    }
  }
}

export default Player;
