const PlayerStates = {
  STAND: "stand",
  RUN: "run",
  JUMP: "jump",
}

const PlayerFrames = {
  STAND: {
    height: 32,
    width: 32,
    offsetX: 160,
    offsetY: 68,
    count: 1,
  },
  RUN: {
    height: 32,
    width: 32,
    offsetX: 194,
    offsetY: 68,
    count: 3,
  },
  JUMP: {
    height: 32,
    width: 32,
    offsetX: 330,
    offsetY: 68,
    count: 1,
  },
}

const SHEET_GAP = 2;

class Player {
  constructor(context, sheet) {
    this.context = context;
    this.sheet = sheet;

    this.state = PlayerStates.JUMP;

    this.frameIndex = 0;
    this.tickCount = 0;
    this.ticksPerFrame = 4;
  }

  setPlayerState(state) {
    this.state = state;
  }

  update() {
    const frameInfo = this.getFrameInfo();
    this.tickCount++;
    if (this.tickCount > this.ticksPerFrame) {
      this.tickCount = 0;
      this.frameIndex++;
      this.frameIndex = (this.frameIndex < frameInfo.count)? this.frameIndex : 0; 
    }
  }

  getFrameInfo() {
    let frame = PlayerFrames.STAND;
    if (this.state === PlayerStates.STAND) { 
      frame = PlayerFrames.STAND;
    } else if (this.state === PlayerStates.RUN) {
      frame = PlayerFrames.RUN;
    } else if (this.state === PlayerStates.JUMP) {
      frame = PlayerFrames.JUMP;
    }
    return frame;
  }

  imgCache = {}
  getSheetImage(sheet, x, y, width, height, direction) {

    canvas = document.createElement('canvas');
    canvasContext = canvas.getContext('2d');

    canvasContext.translate(width, 0);
    canvasContext.scale(-1, 1);
    this.canvasContext.drawImage(image, 0, 0);
  }

  draw() {
    const frameInfo = this.getFrameInfo();
    // get frame based on index
    const frameX = frameInfo.offsetX + ((frameInfo.width + SHEET_GAP) * this.frameIndex);

    this.context.drawImage(this.sheet,
                           frameX, frameInfo.offsetY, frameInfo.width, frameInfo.height,
                           0, 0, frameInfo.width, frameInfo.height);
  }
}

export default Player;
