class Player {
  height = 16;
  width: 16;
  offsetX = 80;
  offsetY = 34;
  gap = 1;

  constructor(context, sheet) {
    this.context = context;
    this.sheet = sheet;
  }

  draw() {
    this.context.drawImage(this.sheet,
                           80, 34, this.width, this.height,
                           0, 0, this.width, this.height);
  }
}

export default Player;
