import Player from './Player';
import Connection from './Connection'


class Game {

  constructor(width, height, context) {
    this.width = width;
    this.height = height;
    this.context = context;
  }

  setResources(resources) {
    this.resources = resources;
  }

  init() {
    // make sure resources are loaded
    Promise.all(Object.values(this.resources).map(img => this.checkImage(img.src)))
                  .then(() => this.start());

    // create connection class
    this.connection = new Connection()
  }

  start() {
    const player = new Player(this.context, this.resources.mario);
    this.player = player;

    // create connection for player
    this.connection.connect("playerOne")
    this.subscribe((action) => {

    })

    document.onkeydown = (e) => {
        e = e || window.event;

        if (e.keyCode === 38) {
            // up arrow
           //player.state = 'jump';
           this.connection.send("up")
        }
        else if (e.keyCode === 40) {
            // down arrow
           //player.state = 'stand';
           this.connection.send("down")
        }
        else if (e.keyCode === 37) {
           // left arrow
           //player.direction = 'left';
           //player.state = 'run';
           this.connection.send("left")
        }
        else if (e.keyCode === 39) {
           // right arrow
           //player.direction = 'right';
           //player.state = 'run';
           this.connection.send("right")
        }

    }

    this.gameLoop();
  }

  drawBG() {
    this.context.fillStyle = "#5D94FB";
    this.context.fillRect(0, 0, this.width, this.height);
  }

  // main game loop -------------------------
  gameLoop () {

    window.requestAnimationFrame(() => this.gameLoop());

    this.drawBG();
    this.player.update();
    this.player.draw();
  }

  // UTILS ----------------------------------
  checkImage(path) {
    return new Promise(resolve => {
        const img = new Image();
        img.onload = () => resolve({path, status: 'ok'});
        img.onerror = () => resolve({path, status: 'error'});

        img.src = path;
    });
  }

}

export default Game;
