class Connection {
  connect(playerName) {
    this.ws = new WebSocket(`ws://localhost:8080/?player=${playerName}`);

    this.ws.onopen = (event) => {
      this.ws.send("Here's some text that the server is urgently awaiting!", event);
    };
  }

}

export default Connection
